import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux';
import { getArticles } from '../actions/index';
import { Link } from 'react-router-dom'
import { ListGroup, ListGroupItem} from 'react-bootstrap';
import articles_reducer from '../reducers/articles_reducer';


console.log(getArticles());

class Articles extends Component {
    constructor(props){
        super(props);
        console.log(props);
        this.state = {
            articles: []
        }
    }
     componentDidMount(){
        getArticles();
        console.log(this.props.getArticles());

    }

    async componentWillMount(){
        this.props.getArticles();
        // console.log(this.props.articles());
    //    const response = await fetch(`http://localhost:5000/api/v1/articles/`)
    //     .then(res => res.json())
    //     .then( json => 
    //     this.setState({
    //         articles: [...json]
    //     }))
    }

    displayArticles(){
        console.log(this.props.articles)
        return this.props.articles.map((article) => { 
            return (
                <li key={article.id}>
                    <h4>{article.title}</h4> 
                </li>
                    )
             })

    }   
    render() {
    return (
      <div className="container">
        <br/>
        <div className="">
             <Link  className="btn btn-success" to="articles/new">
                Add an article 
            </Link>
         </div>

        <h1>Articles should apear here:</h1>
        <ul>
        {this.displayArticles()}
        </ul>
      </div>
    )
    }
}

function mapStateToProps(state){
    console.log(state.articles.all)
    return {
        articles: state.articles.all
    };
}

export default connect(mapStateToProps, { getArticles : getArticles })(Articles);
