import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux';
import { getArticles } from '../actions/index';
import { Link } from 'react-router-dom'
import { ListGroup, ListGroupItem} from 'react-bootstrap';
import articles_reducer from '../reducers/articles_reducer';

let array = [];
const request =  axios.get(`http://localhost:5000/api/v1/articles`).then(res => {
    let data = res.data;
    console.log(data);
    array = data;
    return data
})

async function waitForPromise(){
    let result = await Promise.resolve(request);
     array = result;
     return array;
}
waitForPromise();

console.log(array);

