//     return {
//         type: GET_ARTICLES,
//         payload: request
//     }
// }

class Articles extends Component {
    constructor(props){
        super(props);
    }

    componentWillMount(){
        console.log(this.props.articles);
        console.log(this.props.getArticles());
        console.log(this.props);
        console.log(request)
    }

    displayArticles(){
        let result = axios.get(`http://localhost:5000/api/v1/articles`).then(res => res.json).then(data => {
            console.log(data)
            res.data.map((article) => { 

                return (
                    <li key={article.id}>
                        <h4>{article.title}</h4> 
                    </li>
                        )
                 })
        })
        return result;
    }   
    render() {
    return (
      <div className="container">
        <br/>
        <div className="">
             <Link  className="btn btn-success" to="articles/new">
                Add an article 
            </Link>
         </div>

        <h1>Articles should apear here:</h1>
        <ul>
        {this.displayArticles()}
        </ul>
      </div>
    )
    }
}

function mapStateToProps(state){
    state.articles.all = array;
    console.log(state.articles.all)
    return {
        articles:request
    };
}

export default connect(mapStateToProps , {getArticles})(Articles);
