import { GET_ARTICLES , CREATE_ARTICLE} from './types';
import  axios from 'axios'

const API_URL = 'http://localhost:5000/api/v1';


//returns 
 const getArticles =  () => {
    try {
        return  axios.get(`${API_URL}/articles`)
    } catch(error){
        console.log(error)
    }
}

export function articles() {
    const request = new Promise((resolve, reject) => {
        axios.get(`${API_URL}/articles`).then(res => resolve(res.data))
    });

    return {
        type: GET_ARTICLES,
        payload: request
    }
}

export function createArticle(props) {
    const request =  axios.post(`${API_URL}/articles`, props)

    console.log(request);

    return {
        type: CREATE_ARTICLE,
        payload: request
    }
}
