import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux';
import { articles } from '../actions/index';
import { Link } from 'react-router-dom'
import { ListGroup, ListGroupItem} from 'react-bootstrap';




class Articles extends Component {
    componentWillMount(){
        this.props.articles();
    }
    render() {
    return (
      <div className="container">
        <br/>
        <div className="">
             <Link  className="btn btn-success" to="articles/new">
                Add an article 
            </Link>
         </div>

        <h1>Articles should apear here:</h1>
        <ListGroup>
            {this.props.articles.map((article) => <ListGroupItem> {article.title} </ListGroupItem>)}
        </ListGroup>
      </div>
    )
  }
}

function mapStateToProps(state){
    return{
        article: state.articles.all
    }
}

export default connect(mapStateToProps, { articles })(Articles);
