import { ListGroup, ListGroupItem} from 'react-bootstrap';


const API_URL = 'http://localhost:5000/api/v1';





class Articles extends Component {
    constructor(props){
        super(props);
    }

    componentWillMount(){
        console.log(request);
        this.props.articles();
    }

    displayArticles(){
        const request =  axios.post(`${API_URL}/articles`, this.props)
        console.log(request);
        return this.props.articles.map((article) => { 
        return (
            <li key={article.id}>
                <h4>{article.title}</h4> 
            </li>
                )
         })
    }   
    render() {
    return (
      <div className="container">
        <br/>
        <div className="">
             <Link  className="btn btn-success" to="articles/new">
                Add an article 
            </Link>
         </div>

        <h1>Articles should apear here:</h1>
        <ul>
        {/* {this.displayArticles()} */}
        </ul>
      </div>
    )
    }
}

function mapStateToProps(state){
    return{
        articles: state.articles.all
    }
}

export default connect(mapStateToProps, { articles })(Articles);
