import { GET_ARTICLES } from './types';
import  axios from 'axios'

const API_URL = 'http://localhost:5000/api/v1';


//returns 
 const getArticles =  () => {
    try {
        return  axios.get(`${API_URL}/articles`)
    } catch(error){
        console.log(error)
    }
}

export function articles() {
    const request =  getArticles();

    return {
        type: GET_ARTICLES,
        payload: request
    }
}
